mapper.select:
select * from ( select rownum RNUM, zxcvb. * from ( SELECT no as "no" ,email as "email" ,title as "title" ,content as "content" ,readCount as "readCount" ,to_char(regDate,'YYYY-MM-DD') as "regDate" ,reportCount as "reportCount" ,commentCount as "commentCount" ,issecret as "issecret" ,partNo as "partNo" FROM QUESTION
order by no desc ,regDate desc
order by ${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

