mapper.select:
SELECT typetitle as "typetitle" ,type as "type" ,no as "no" ,TITLE as "title" ,to_char(regDate,'YYYY-MM-DD') as "regDate" ,EMAIL as "email" FROM( SELECT '문의게시판' AS typetitle ,'2' type ,no ,TITLE ,REGDATE ,EMAIL FROM ASK
UNION ALL
SELECT '자유게시판' AS typetitle ,'1' type ,no ,TITLE ,REGDATE ,EMAIL FROM FREE ) DS where ds.EMAIL = #{sessionEmail}

mapper.update:

 	update 
		memberboard
	set
		pw = #{newPwd}
	 where email = #{email}
